DualQuatd
DualQuatd::GetInverse() const
{
    // DQ * DQ.GetInverse() == GetIdentity()
    const double realLengthSqr = Dot(_real, _real);

    if ( realLengthSqr <= 0.0 )
        return DualQuatd::GetIdentity();

    const double invRealLengthSqr = 1.0 / realLengthSqr;
    const DualQuatd conjInvLenSqr = GetConjugate() * invRealLengthSqr;
    const Quatd realPart = conjInvLenSqr.GetReal();
    const Quatd dualPart = conjInvLenSqr.GetDual() -
        (2.0 * invRealLengthSqr * Dot(_real, _dual) * conjInvLenSqr.GetReal());

    return DualQuatd( realPart, dualPart );
}

