Range2d::Range2d(class Range2f const &other)
    : _min( (other.GetMin()))
    , _max( (other.GetMax()))
{
}

double
Range2d::GetDistanceSquared(const Vec2d &p) const
{
    double dist = 0.0;

    if (p[0] < _min[0]) {
	// p is left of box
	dist += Sqr(_min[0] - p[0]);
    }
    else if (p[0] > _max[0]) {
	// p is right of box
	dist += Sqr(p[0] - _max[0]);
    }
    if (p[1] < _min[1]) {
	// p is front of box
	dist += Sqr(_min[1] - p[1]);
    }
    else if (p[1] > _max[1]) {
	// p is back of box
	dist += Sqr(p[1] - _max[1]);
    }

    return dist;
}

Vec2d
Range2d::GetCorner(size_t i) const
{
    if (i > 3) {
        TF_CODING_ERROR("Invalid corner %zu > 3.", i);
        return _min;
    }

    return Vec2d((i & 1 ? _max : _min)[0], (i & 2 ? _max : _min)[1]);
}

Range2d
Range2d::GetQuadrant(size_t i) const
{
    if (i > 3) {
        TF_CODING_ERROR("Invalid quadrant %zu > 3.", i);
        return Range2d();
    }

    Vec2d a = GetCorner(i);
    Vec2d b = .5 * (_min + _max);

    return Range2d(
        Vec2d(Min(a[0], b[0]), Min(a[1], b[1])),
        Vec2d(Max(a[0], b[0]), Max(a[1], b[1])));
}

