Range3d::Range3d(class Range3f const &other)
    : _min( (other.GetMin()))
    , _max( (other.GetMax()))
{
}

double
Range3d::GetDistanceSquared(const Vec3d &p) const
{
    double dist = 0.0;

    if (p[0] < _min[0]) {
	// p is left of box
	dist += Sqr(_min[0] - p[0]);
    }
    else if (p[0] > _max[0]) {
	// p is right of box
	dist += Sqr(p[0] - _max[0]);
    }
    if (p[1] < _min[1]) {
	// p is front of box
	dist += Sqr(_min[1] - p[1]);
    }
    else if (p[1] > _max[1]) {
	// p is back of box
	dist += Sqr(p[1] - _max[1]);
    }
    if (p[2] < _min[2]) {
	// p is below of box
	dist += Sqr(_min[2] - p[2]);
    }
    else if (p[2] > _max[2]) {
	// p is above of box
	dist += Sqr(p[2] - _max[2]);
    }

    return dist;
}

Vec3d
Range3d::GetCorner(size_t i) const
{
    if (i > 7) {
        TF_CODING_ERROR("Invalid corner %zu > 7.", i);
        return _min;
    }
    return Vec3d(
        (i & 1 ? _max : _min)[0],
        (i & 2 ? _max : _min)[1],
        (i & 4 ? _max : _min)[2]);
}

Range3d
Range3d::GetOctant(size_t i) const
{
    if (i > 7) {
        TF_CODING_ERROR("Invalid octant %zu > 7.", i);
        return Range3d();
    }

    Vec3d a = GetCorner(i);
    Vec3d b = .5 * (_min + _max);

    return Range3d(
        Vec3d(Min(a[0], b[0]), Min(a[1], b[1]), Min(a[2], b[2])),
        Vec3d(Max(a[0], b[0]), Max(a[1], b[1]), Max(a[2], b[2])));
}

